import React from 'react';
import logo from './logo.svg';
import img from './sticker.png';
import './App.css';

function nama(element) {
if (element.nama == "ia") {
  return element.nama + " " + element.kk;
}
  return "maaf slah";
}

const element = {
  nama: 'deni',
  kk:'denot'
}

const tampil=nama(element);


const spec = "gg";
const dandi = <div> tes  {spec}</div>;



function Welcome(props) {
  return <h1>Hello, {props.name}</h1>;
}


function Sum() { 
  return 1+2;
}




// menampilkan gambar dengan funtiom

function Comment (props) { 
        return (
          <div>
          <img style={{ width:'50%'}}
            src={props.author.avatarUrl}
            alt={props.author.name}
          />
            <p>{props.text}</p>
            <p>{props.author.name}</p>
            <p>{new Date().toLocaleDateString()}</p> 
            {/* tanggal */}
          </div>
        )
       }


const comment = {
          date: new Date(),
          text: 'I hope you enjoy learning React!',
          author: {
            name: 'Dandi',
            avatarUrl: 'https://asset-a.grid.id/crop/0x0:0x0/700x0/photo/2018/10/19/2605653088.jpg',
          },
        };






// timer format waktu

function FormattedDate(props) {
  return <h2>It is {props.date.toLocaleTimeString()}.</h2>;
}

class Clock extends React.Component {
  constructor(props) {
    super(props);
    this.state = { 
      date: new Date()
    };

  }


  componentDidMount() {
    this.timerID = setInterval(
      () => this.tick(),
      1000
    );
  }

  componentWillUnmount() {
    clearInterval(this.timerID);
  }

  tick() {
    this.setState({
      date: new Date()
    });
  }

  render() {
    return (
      <div>
        <h1>Hello, world!</h1>
        <FormattedDate date={this.state.date} />
      </div>
    );
  }

}

class Tombol extends React.Component {
  constructor(props) {
    super(props);
    this.state = { isToggleOn: true };

    // This binding is necessary to make `this` work in the callback
    this.handleClick = this.handleClick.bind(this);
  }

 handleClick() {
    this.setState(prevState => ({
      isToggleOn: !prevState.isToggleOn
    }));
  }

  render() {
    return (
      <button onClick={this.handleClick}>
        {this.state.isToggleOn ? 'ON' : <img style={{ width:'50%'}} src={img} />}
      </button>
    );
  }

}



class Pindah extends React.Component{
  constructor(props){
    super(props);
    this.menampilkan = this.menampilkan.bind(this);
    this.kembali = this.kembali.bind(this);
    this.state = {
      tombol:false
    };
  }

  menampilkan(){
    this.setState({})
  }
}

export default function App(props) {




    return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <p>
         {nama(element)} {dandi} {tampil}
        </p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React 
        </a>
        <br />

        <Welcome name="oke" />



        <Comment
          text={comment.text}
          author={comment.author}
        />



      <Sum/>



      <Clock  /> 


         <Tombol />


         <Pindah />

      </header>
    </div>
  );
}
